import react from 'react';
import React from 'react'


/*class BookControl extends React.Component { */
function BookControl({state = {
  value: react.props.book.shelf,
},
handleChange = event => {
  this.setState({ value: event.target.value });
  this.props.onChangeShelf(react.props.book, react.target.value);
}}) {
  


 
    return (
      <div className="book-shelf-changer">
        <select value={BookControl.state.value} onChange={BookControl.handleChange} >
          <option value="move" disabled>Move to...</option>
          <option value="currentlyReading">Currently Reading</option>
          <option value="wantToRead">Want to Read</option>
          <option value="read">Read</option>
          <option value="none">None</option>
        </select>
      </div>
    )
  }


export default BookControl
